import base64
import os

# Base64 data from the console output (without the data:image/png;base64, prefix)
base64_data = ""

# Decode the base64 data
image_data = base64.b64decode(base64_data)

# Save as favicon.ico in the blog directory
with open(os.path.join(os.path.dirname(__file__), "favicon.ico"), "wb") as f:
    f.write(image_data)

print("Favicon created successfully!")
